const hyper = require('hyperhtml/index')
const {bind, wire} = hyper

const HorizontalNavigation = require('components/horizontal_navigation')
const {CurrentView, NotFoundView, SystemsView} = require('views')

const {layout} = require('styles')
const navigation = require('navigation')

module.exports = function Layout () {
  const {row, col3, col9} = layout

  return wire()`
  <div class="${row}">

    <div class="${col3}">
      ${HorizontalNavigation({navigation})}
    </div>

    <div class="${col9}">
      ${CurrentView()}
    </div>

  </div>`
}
