const hyper = require('hyperhtml/index')
const {bind, wire} = hyper

module.exports = class SimpleTh extends hyper.Component {
  get defaultState () {
    return {}
  }

  constructor (props) {
    super()
    this.props = props
  }

  render () {
    return this.html`<td>
      ${this.props.value}
    </td>`
  }
}
