const hyper = require('hyperhtml/index')
const {bind, wire} = hyper

const SimpleTd = require('./SimpleTd')

module.exports = class SimpleTr extends hyper.Component {
  get defaultState () {
    return {}
  }

  constructor (props) {
    super()
    this.props = props
  }

  render () {
    return this.html`<tr>
      ${this.props.columns.map(column => new SimpleTd({value: this.props.item[column.property || column.title]}))}
    </tr>`
  }
}
