const csjs = require('csjs')

const vars = require('styles/vars')
const {common} = require('styles')

module.exports = csjs`
table {
  width: 100%;
}

.table {
  border: 1px solid red;
}

table tbody tr td {
  padding: 0 ${vars.spacing.minor};
}
table tbody tr:hover td {
  background: #ddd;
}

table thead th {
  border-bottom: 1px solid silver;
}
table thead th:not(:last-child) {
  border-right: 1px solid silver;
}

.th extends ${common.unselectable} {}
.sortedUp {background-color: blue;}
.sortedDown {background-color: yellow;}

.selected {
  color: red;
}
`
